<template>    <div class="comment">        <div class="avatar">            <n-avatar                round                :size="48"                :src="comment.user.avatar"            />        </div>        <div class="content">            <div>                <span class="username">{{comment.user.username}}ï¼š </span>                <span class="comment-content">{{comment.content}}</span>            </div>            <div class="footer">                <n-space justify="space-between">                    <span class="comment-time">{{comment.createTime.replace('T', ' ')}}</span>                    <n-button @click="star(comment.id)" style="color: #b0b1b2;font-size:10px" text>                        <n-icon>                            <svg xmlns="http://www.w3.org/2000/svg"                                 xmlns:xlink="http://www.w3.org/1999/xlink"                                 viewBox="0 0 32 32">                                <path                                    d="M26 12h-6V6a3.003 3.003 0 0 0-3-3h-2.133a2.01 2.01 0 0 0-1.98 1.717l-.845 5.917L8.465 16H2v14h21a7.008 7.008 0 0 0 7-7v-7a4.005 4.005 0 0 0-4-4zM8 28H4V18h4zm20-5a5.006 5.006 0 0 1-5 5H10V17.303l3.958-5.937l.91-6.366H17a1 1 0 0 1 1 1v8h8a2.002 2.002 0 0 1 2 2z"                                    fill="currentColor"></path>                            </svg>                        </n-icon>                        <span>&nbsp;{{comment.star}}</span>                    </n-button>                </n-space>            </div>        </div>    </div></template><script>import {NAvatar, NButton, NIcon, NSpace, useMessage} from 'naive-ui'import request from '../utils/request'export default {    name: 'Comment',    components: {        NAvatar,        NButton,        NIcon,        NSpace,    },    props: ['comment', 'starUrl'],    setup(props){        let message = useMessage()        function star(id){            request.get(props.starUrl + id).then(res => {                if(res.data.code === 0){                    message.success(res.data.data)                }else {                    message.error(res.data.msg)                }            })        }        return {            star        }    }}</script><style scoped>.comment{    display: flex;    border-bottom: 1px solid #e8eaea;    padding: 10px 0;}.content{    padding: 0 20px;    display: flex;    flex-direction: column;}.username{    color: #0ea5e9;}.footer{    color: #b0b1b2;    font-size: 12px;    display: flex;    justify-content: space-between;}</style>