<template>    <p class="recommend">推荐音乐</p>    <n-grid x-gap="12" :y-gap="8" cols="500:2 600:3">        <n-gi v-for="num in 12" v-if="isLoading">            <n-skeleton height="80px"/>        </n-gi>        <n-gi v-else v-for="item in songList">            <n-list @dblclick="play(item)" bordered>                <n-list-item>                    <template #prefix>                        <n-image                            width="60"                            lazy                            :src="item.imgSrc"                        />                    </template>                    <div>                        <n-ellipsis style="max-width: 80px;font-size: 18px">                            {{item.name}}                        </n-ellipsis>                        <br>                        <n-ellipsis style="max-width: 200px;color: #848687">                            <span v-for="(singer, index) in item.singers">                                <span v-if="index !== 0"> / </span><span>{{ singer.name }}</span>                            </span>                        </n-ellipsis>                    </div>                </n-list-item>            </n-list>        </n-gi>    </n-grid>    <n-empty v-if="isLoading === false && songList.length === 0" description="暂无数据">    </n-empty></template><script>import {onMounted, reactive, ref} from 'vue'import {NGi, NGrid, NListItem, NButton, NList, NImage, NThing, NSkeleton, NEmpty, NEllipsis} from 'naive-ui'import request from '../utils/request'import {usePlayStore} from '../store'export default {    name: 'RecommendSong',    components: {        NGi,        NGrid,        NListItem,        NButton,        NList,        NImage,        NThing,        NSkeleton,        NEmpty,        NEllipsis,    },    setup(){        let isLoading = ref(true)        let songList = reactive([])        let playStore = usePlayStore()        onMounted(()=>{            request.get('/song/newSong').then(res => {                if(res.data.code === 0){                    songList.push(...res.data.data)                    isLoading.value = false                }            })        })        function play(item){            playStore.play(songList, item)        }        return {            isLoading,            songList,            play,        }    }}</script><style scoped>.recommend {    font-size: 1.25rem;    line-height: 1.75rem;}.n-list:hover{    cursor: pointer;    background-color: #c8c8c8;}</style>