<template>    <p class="recommend">推荐歌单</p>    <n-grid x-gap="12" :y-gap="8" cols="3 500:4 800:6">        <n-gi v-for="num in 10" v-if="isLoading">            <n-skeleton height="258px" width="100%"/>        </n-gi>        <n-gi v-else v-for="item in items">            <RecommendList :key="item.id" :id="item.id" :title="item.name" :imgSrc="item.imgSrc"/>        </n-gi>    </n-grid>    <n-empty v-if="isLoading === false && items.length === 0" description="暂无数据">    </n-empty></template><script>import RecommendList from './RecommendList'import {NGrid, NGi, NSkeleton, NEmpty} from 'naive-ui'import {onMounted, reactive, ref} from 'vue'import request from '../utils/request'export default {    name: 'RecommendSongList',    components: {        RecommendList, NGi, NGrid, NSkeleton, NEmpty    },    setup() {        let isLoading = ref(true)        let items = reactive([]);        onMounted(() => {            request.get('/songList/getRecommend').then(res => {                if (res.data.code === 0) {                    items.push(...res.data.data)                    isLoading.value = false                }            })        })        return {            isLoading,            items        }    }}</script><style scoped>.recommend {    font-size: 1.25rem;    line-height: 1.75rem;}</style>