<template>    <section>        <div class="container" ref="container">            <div class="user singinBx">                <div class="imgBx"></div>                <div class="formBx">                    <form action="/" onsubmit="return false">                        <h2>Sign In</h2>                        <input type="text" v-model="login.username" name="username" placeholder="Username">                        <input type="password" v-model="login.password" name="password" placeholder="Password">                        <div class="error" v-text="LoginError"></div>                        <input type="submit" value="Login" @click="Login">                        <p class="signup">Don't have an account ?<a href="javascript:void(0)" @click="toggleForm">Sign                            Up.</a></p>                    </form>                </div>            </div>            <div class="user singupBx">                <div class="formBx">                    <form action="/" onsubmit="return false">                        <h2>Create an Account</h2>                        <input type="text" v-model="register.username" name="username" placeholder="Username">                        <input type="password" v-model="register.password" name="password"                               placeholder="Create Password">                        <input type="text" v-model="register.phone" name="phone" placeholder="Phone">                        <n-avatar @click="chooseImg" :size="100" :src="register.avatar" :fallback-src="notImage"/>                        <input ref="file" type="file" class="hidden" @change="uploadImg">                        <div class="error" v-text="RegisterError"></div>                        <input type="submit" value="Sign up" @click="Register">                        <p class="signup">Already have an account ?<a href="javascript:void(0)" @click="toggleForm">Sign                            In.</a></p>                    </form>                </div>                <div class="imgBx"></div>            </div>        </div>    </section></template><script>import notImage from '../assets/notImage.jpg'import request from '@/utils/request'import {useMessage, NAvatar} from 'naive-ui'import {ArchiveOutline as ArchiveIcon} from '@vicons/ionicons5'import {reactive, ref, onMounted, getCurrentInstance} from 'vue'import {useRouter} from 'vue-router'export default {    name: 'Login',    setup() {        let router = useRouter()        let container = null;        let login = reactive({            username: '',            password: '',        })        let register = reactive({            username: '',            password: '',            phone: '',            avatar: '',        })        let LoginError = ref('')        let RegisterError = ref('')        let file = null        let message = useMessage()        onMounted(()=>{            container = getCurrentInstance().refs.container            file = getCurrentInstance().refs.file        })        function Login() {            LoginError.value = ''            request.post('/user/login', login).then((res) => {                if (res.data.code === 1) {                    LoginError.value = res.data.msg                } else {                    localStorage.setItem('user', JSON.stringify(res.data.data.user))                    localStorage.setItem('token', res.data.data.token)                    router.push('/')                }            })        }        function Register() {            RegisterError.value = ''            request.post('/user/register', register).then((res) => {                if (res.data.code === 1) {                    RegisterError.value = res.data.msg                } else {                    location.reload()                }            })        }        function toggleForm() {            container.classList.toggle('active')        }        function chooseImg() {            file.click()        }        function uploadImg() {            if (!(file.files[0].type === "image/png") && !(file.files[0].type === "image/jpeg")) {                message.error("只能上传png/jpeg格式的图片文件，请重新上传")                return false            }            if (!(file.files[0].size / 1024 / 1024 < 2)) {                message.error('上传头像图片大小不能超过 2MB!');                return false            }            const param = new FormData()            param.append('file', file.files[0])            request.post('/file/uploadAvatar', param, {headers: {"Content-Type": "multipart/form-data"}}).then(res => {                console.log(res.data)                if (res.data.code === 0) {                    register.avatar = res.data.data                }            })        }        return {            login,            register,            LoginError,            RegisterError,            Login,            Register,            toggleForm,            chooseImg,            uploadImg,            notImage,        }    },    components: {        ArchiveIcon,        NAvatar,    },}</script><style scoped>.hidden {    display: none;}* {    box-sizing: border-box;    font-family: "Poppins", sans-serif;}section {    min-height: 100vh;    background: #d9d9d9;    display: flex;    justify-content: center;    align-items: center;    padding: 20px;}section .container {    position: relative;    width: 800px;    height: 550px;    background: #fff;    box-shadow: 0 15px 50px rgba(0, 0, 0, 0.1);    overflow: hidden;}section .container .user {    position: absolute;    top: 0;    left: 0;    width: 100%;    height: 100%;    display: flex;}section .container .user .imgBx {    position: relative;    width: 50%;    height: 100%;    background: #97dbdc;    transition: .5s;}section .container .user .imgBx img {    position: absolute;    top: 0;    left: 0;    width: 100%;    height: 100%;    object-left: cover;}section .container .user .formBx {    position: relative;    width: 50%;    height: 100%;    background: #fff;    display: flex;    justify-content: center;    align-items: center;    padding: 40px;    transition: .5s;}section .container .user .formBx form h2 {    font-size: 18px;    font-weight: 600;    text-transform: uppercase;    letter-spacing: 2px;    text-align: center;    width: 100%;    margin-bottom: 10px;    color: #555;}section .container .user .formBx form input {    position: relative;    width: 100%;    padding: 10px;    background: #f5f5f5;    color: #333;    border: none;    outline: none;    box-shadow: none;    margin: 8px 0;    font-size: 14px;    letter-spacing: 1px;}section .container .user .formBx form input[type="submit"] {    max-width: 100px;    background: #677eff;    color: #fff;    cursor: pointer;    font-size: 14px;    font-weight: 500;    letter-spacing: 1px;    transition: .5s;}section .container .user .formBx form .signup {    position: relative;    margin-top: 20px;    font-size: 12px;    letter-spacing: 1px;    color: #555;    text-transform: uppercase;    font-weight: 300;}section .container .user .formBx form .signup a {    font-weight: 600;    text-decoration: none;    color: #67eff;}section .container .singupBx {    pointer-events: none;}section .container.active .singupBx {    pointer-events: initial;}section .container .singupBx .formBx {    left: 100%;}section .container.active .singupBx .formBx {    left: 0;}section .container .singupBx .imgBx {    left: -100%;}section .container.active .singupBx .imgBx {    left: 0;}section .container .singinBx .formBx {    left: 0;}section .container.active .singinBx .formBx {    left: 100%;}section .container .singinBx .imgBx {    left: 0;}section .container.active .singinBx .imgBx {    left: 100%;}@media (max-width: 991px) {    section .container {        max-width: 400px;    }    section .container .imgBx {        display: none;    }    section .container .user .formBx {        width: none;    }}.error {    color: red;    font-size: 12px;}</style>