<template><div class="search-result">    <n-layout has-sider>        <n-layout-sider :class=" playStore.isDark ? 'background' : ''" width="100%">            <h2>搜索 {{ keyword }}</h2>            <n-tabs animated>                <n-tab-pane name="单曲" tab="单曲">                    <Songs :keyword="keyword"/>                </n-tab-pane>                <n-tab-pane name="歌单" tab="歌单">                    <SongLists :keyword="keyword"/>                </n-tab-pane>            </n-tabs>        </n-layout-sider>    </n-layout></div></template><script>import {useRoute} from 'vue-router'import {NLayoutSider, NLayout, NTabs, NTabPane} from 'naive-ui'import Songs from '../components/Songs'import {onMounted, ref, watch} from 'vue'import SongLists from '../components/SongLists'import {usePlayStore} from '../store'export default {    name: 'SearchResult',    setup(){        let route = useRoute()        let playStore = usePlayStore()        let keyword = ref(route.query.keyword)        watch(() => route.query.keyword, (newVal) => {            keyword.value = newVal;        });        return {            keyword,            playStore,        }    },    components: {        SongLists,        Songs,        NLayoutSider,        NLayout,        NTabs,        NTabPane,    }}</script><style scoped>.search-result{    padding: 0 24px;}.background{    background-color: #101015;}</style>